import React, { useState } from "react";
import axios from "axios";
import { LoginRequest, LoginResponse, RegisterRequest } from "../models/types";

const TableCity: React.FC = () => {
    const [username, setUsername] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const [role, setRole] = useState<string>("");
    const [message, setMessage] = useState<string>("");

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();

        const loginData: RegisterRequest = { username, password, role};

        try {
            const response = await axios.post<LoginResponse>(
                "http://localhost:8080/auth/register",
                loginData
            );

            const { token } = response.data;
            localStorage.setItem("authToken", token); // Сохранение токена
            setMessage("Login successful!");
        } catch (error: any) {
            const errorMessage = error.response?.data?.message || error.message;
            setMessage(`Login failed: ${errorMessage}`);
        }
    };

    return (
        <div>
            <h1>Регистрация</h1>
            <form onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <input
                    type="role"
                    placeholder="Role"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    required
                />
                <button type="submit">Регистрация</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default TableCity;
