// CompetitionDetails.tsx

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

interface Gymnast {
  id: number;
  name: string;
  birth_year: number;
  club: string;
}

interface Judge {
  id: number;
  name: string;
  category: string;
}

interface CompetitionData {
  id: number;
  name: string;
  date: string;
  location: string;
  gymnasts: Gymnast[];
  judges: Judge[];
}

const CompetitionDetails: React.FC = () => {
  const { id } = useParams(); // Получаем :id из URL
  const [competition, setCompetition] = useState<CompetitionData | null>(null);
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    if (id) {
      fetchCompetitionDetails(id);
    }
  }, [id]);

  const fetchCompetitionDetails = async (competitionId: string) => {
    try {
      const response = await axios.get<CompetitionData>(
        `http://localhost:8080/competitions/${competitionId}`
      );
      setCompetition(response.data);
    } catch (error) {
      console.error("Ошибка при получении информации о соревновании:", error);
    } finally {
      setLoading(false);
    }
  };

  const handleJudgeClick = () => {
    // Здесь можно добавить логику судейства.
    // Например, перейти на страницу судейства /scores/:competitionId или открыть модалку.
    console.log("Нажата кнопка 'Судить'");
  };

  if (loading) {
    return <p>Загрузка...</p>;
  }

  if (!competition) {
    return <p>Соревнование не найдено.</p>;
  }

  return (
    <div>
      <h2>Детальная информация о соревновании</h2>
      <p>ID: {competition.id}</p>
      <p>Название: {competition.name}</p>
      <p>Дата: {competition.date}</p>
      <p>Место: {competition.location}</p>

      <h3>Список гимнасток</h3>
      {competition.gymnasts.length > 0 ? (
        <ul>
          {competition.gymnasts.map((gymnast) => (
            <li key={gymnast.id}>
              {gymnast.name} (Год рождения: {gymnast.birth_year}), клуб:{" "}
              {gymnast.club}
            </li>
          ))}
        </ul>
      ) : (
        <p>Гимнасток нет.</p>
      )}

      {/* Кнопка "Судить" одна для всего соревнования */}
      <button onClick={handleJudgeClick}>Судить</button>
    </div>
  );
};

export default CompetitionDetails;
